const TARGET_UTC=new Date(Date.UTC(2021,3,22,12,30,0));const FACTOR_SEC=1000;const FACTOR_MIN=FACTOR_SEC*60;const FACTOR_HRS=FACTOR_MIN*60;const FACTOR_DAY=FACTOR_HRS*24;function formatNum(number){return number.toString().padStart(2,'0');}let interval=setInterval(()=>{const now=new Date().getTime();const distance=TARGET_UTC.getTime()-now;if(distance<0){document.getElementById("tpc_pac_001__timer").innerHTML="00 : 00 : 00 : 00";clearInterval(interval);}else{const days=Math.floor(distance/FACTOR_DAY);const hours=Math.floor((distance%FACTOR_DAY)/FACTOR_HRS);const minutes=Math.floor((distance%FACTOR_HRS)/FACTOR_MIN);const seconds=Math.floor((distance%FACTOR_MIN)/FACTOR_SEC);document.getElementById("tpc_pac_001__timer").innerHTML=formatNum(days)+" : "+formatNum(hours)+" : "+formatNum(minutes)+" : "+formatNum(seconds);}},1*FACTOR_SEC);